class Myon
{
    public Myon() { }
    public static int Main()
    {
        new Myon().calc();
        return 0;
    }


    Scanner cin;

    const int MAX = 10001000;
    bool[] prime = new bool[MAX];
    void setPrime()
    {
        prime[0] = prime[1] = true;
        for (int i = 2; i < MAX; i++)
        {
            if (!prime[i])
            {
                for (int j = i + i; j < MAX; j+=i)
                {
                    prime[j] = true;
                }
            }
        }
    }


    void calc()
    {
        cin = new Scanner();
        setPrime();
        int N = cin.nextInt();
        int[] A = cin.ArrayInt(N);

        List<int> start = new List<int>();
        List<int> num = new List<int>();

        int pre = -2;
        int cnt = 0;

        foreach (var now in A)
        {
            if(now != pre + 1)
            {
                if (pre != -2)
                {
                    start.Add(pre - cnt);
                    num.Add(cnt);
                }
                cnt = 0;
            }
            pre = now;
            cnt++;
        }
        start.Add(pre - cnt);
        num.Add(cnt);

        int M = start.Count;


        int[,] dist = new int[M, M];
        for (int i = 0; i < M; i++)
        {
            for (int j = i + 1; j < M; j++)
            {
                dist[i, j] = dist[j, i] = -getCost(start[j] - start[i] - num[i]);
                dist[i, j] = dist[j, i] -= getCost(start[j] - start[i] + num[j]);
            }
            dist[i, i] = -getCost(num[i]) * 2;
        }


        long ans = hungarian(dist);
        Console.WriteLine(ans / -2);

    }

    int getCost(int A)
    {
        if (A % 2 == 0) return 2;
        else if (!prime[A]) return 1;
        else return 3;
    }


    long hungarian(int[,] a)
    {
        int n = a.GetLength(0);
        long p, q;
        long[] fx = new long[n];
        long[] fy = new long[n];
        long inf = (long)1e16;
        long i, j, k;
        for (i = 0; i < n; i++) fx[i] = inf;
        for (i = 0; i < n; i++) fy[i] = inf;
        long[] x = new long[n];
        long[] y = new long[n];
        for (i = 0; i < n; i++) x[i] = y[i] = -1;

        for (i = 0; i < n; ++i)
            for (j = 0; j < n; ++j)
                fx[i] = Math.Max(fx[i], a[i, j]);
        for (i = 0; i < n;)
        {
            long[] t = new long[n];
            long[] s = new long[n + 1];
            for (j = 0; j < n; j++) t[j] = -1;
            for (j = 0; j <= n; j++) s[j] = i;
            for (p = q = 0; p <= q && x[i] < 0; ++p)
                for (k = s[p], j = 0; j < n && x[i] < 0; ++j)
                    if (fx[k] + fy[j] == a[k, j] && t[j] < 0)
                    {
                        s[++q] = y[j];
                        t[j] = k;
                        if (s[q] < 0)
                            for (p = j; p >= 0; j = p)
                            {
                                y[j] = k = t[j];
                                p = x[k];
                                x[k] = j;
                            }
                    }
            if (x[i] < 0)
            {
                long d = inf;
                for (k = 0; k <= q; ++k)
                    for (j = 0; j < n; ++j)
                        if (t[j] < 0) d = Math.Min(d, fx[s[k]] + fy[j] - a[s[k], j]);
                for (j = 0; j < n; ++j)
                {
                    if (t[j] >= 0) fy[j] += d;
                }
                for (k = 0; k <= q; ++k) fx[s[k]] -= d;
            }
            else ++i;
        }
        long ret = 0;
        for (i = 0; i < n; ++i) ret += a[i, x[i]];
        return ret;
    }
}


