class Solve{
    public Solve(){}
    StringBuilder sb;
    public static int Main(){
        new Solve().Run();
        return 0;
    }
    void Run(){
        sb = new StringBuilder();
        Calc();
        Console.Write(sb.ToString());
    }
    void Calc(){
        int N = int.Parse(Console.ReadLine());
        string[] str = Console.ReadLine().Split(' ');
        int[] X = new int[N];
        for(int i=0;i<N;i++){
            X[i] = int.Parse(str[i]);
        }
        List<int> Diff = new List<int>();
        Diff.Add(X[0]);
        Diff.Add(X[N-1]+1);
        for(int i=1;i<N;i++){
            if(X[i] != X[i-1] + 1){
                Diff.Add(X[i]);
            }
        }
        for(int i=0;i<N-1;i++){
            if(X[i] + 1 != X[i+1]){
                Diff.Add(X[i]+1);
            }
        }
        int[] diff = Diff.ToArray();
        int o = 0;
        int e = 0;
        for(int i=0;i<diff.Length;i++){
            if(diff[i] % 2 == 0){
                e++;
            }
            else{
                o++;
            }
        }
        int[] Odd = new int[o];
        int[] Even = new int[e];
        o = 0;
        e = 0;
        for(int i=0;i<diff.Length;i++){
            if(diff[i] % 2 == 0){
                Even[e] = diff[i];
                e++;
            }
            else{
                Odd[o] = diff[i];
                o++;
            }
        }
        List<int> f = new List<int>();
        List<int> t = new List<int>();
        List<long> capa = new List<long>();
        int S = 0;
        int Ef = 1;
        int Of = e+1;
        int T = o+e+1;
        for(int i=0;i<e;i++){
            f.Add(S);
            t.Add(Ef+i);
            capa.Add(1);
        }
        for(int i=0;i<0;i++){
            f.Add(Of+i);
            t.Add(T);
            capa.Add(1);
        }
        Generateprimenumber G = new Generateprimenumber(10000);
        for(int i=0;i<e;i++){
            for(int j=0;j<o;j++){
                if(G.Prime(Math.Max(Even[i]-Odd[j],Odd[j]-Even[i]))){
                    f.Add(Ef+i);
                    t.Add(Of+j);
                    capa.Add(1);
                }
            }
        }
        MaxFlow Flow = new MaxFlow(f.ToArray(),t.ToArray(),capa.ToArray(),o+e+2,0,o+e+1);
        int flow = (int)Flow.flow;
        int count = diff.Length - flow;
        if(e % 2 != flow % 2){
            count++;
        }
        sb.Append(count+"\n");
    }
}
