fn prime(n: usize) -> Vec<bool> {
    let mut ret = vec![true; n];
    ret[0] = false;
    ret[1] = false;
    for i in 2..n {
        if !ret[i] {
            continue
        }
        let mut ii = i * i;
        while ii < n {
            ret[ii] = false;
            ii += i;
        }
    }
    ret
}

fn main() {
    let n: usize = read();
    let x: Vec<usize> = readnc();

    let mut fpos = vec![];
    fpos.push(x[0]);

    for i in 1..n {
        if x[i-1] + 1 < x[i] {
            fpos.push(x[i-1]+1);
            fpos.push(x[i])
        }
    }
    fpos.push(x[n-1]+1);

    let isp = prime(10000100);

    let n = fpos.len();
    let mut even = 0;
    let mut odd = 0;
    for &f in &fpos {
        if f % 2 == 0 {
            even += 1;
        } else {
            odd += 1;
        }
    }

    let mut flow = FlowGraph::new(n + 2);
    let source = n;
    let sink = n + 1;
    for i in 0..n {
        for j in 0..n {
            if i != j || fpos[i] % 2 == 0 && fpos[j] % 2 == 1 {
                flow.edge(i, j, 1);
            }
        }
    }

    let pair = flow.max_flow(source, sink);
    let even_left = even - pair;
    let odd_left = odd - pair;
    println!("{}", pair + (even_left / 2) * 2 + (odd_left / 2) * 2 + max(0, odd_left % 2) * 3);
}

