      if (ret.X == 1) ret.X = 0;
      else ret.X = res.Y;

      if (ret.Y == 1) {
        assert(!spe);
        ret.Y = 0;
      } else if (spe) {
        assert(ret.Y == 0);
        ret.Y = !res.X;
      } else {
        ret.Y = res.X;
      }
      spe = false;
    }
    first = false;
  }
