template<class F> ll mgr(ll ok, ll ng, F f) {
    bool han = true;
    if (ok < ng)
        while (ng - ok > 1) {
            ll mid = (ok + ng) >> 1;
            if (f(mid))ok = mid, han = true; else ng = mid, han = false;
            deb(mid,han);
        }
    else
        while (ok - ng > 1) {
            ll mid = (ok + ng) >> 1;
            if (f(mid))ok = mid, han = true; else ng = mid, han = false;
            deb(mid,han);
        }
    return ok;
}
