template<class T> bool nibu( graph<T> &g) {    int size = 0;    rep(i, g.n)size += sz(g.g[i]);    if (size == 0)return true;    unionfind uf(g.n * 2);    rep(i, g.n)fora(e, g.g[i])uf.unite(e.f, e.t + g.n), uf.unite(e.f + g.n, e.t);    rep(i, g.n)if (uf.same(i, i + g.n))return 0;    return true;}
// O(E)
template<class T> bool nibu_sub( graph<T> &g) {    umapi col;/*0 */   queue<int> q;    /*0111 */    fora(e, g.edegs) {        /*ft*/        if (col[e.f] == 0)q.push(e.f);        while (!q.empty()) {            int f = q.front();            q.pop();            int fc = col[f];            forg(gi, g[f]) {                int &tc = col[t];                /*fc*/                if (fc == tc)return false;                /**/                if (tc)continue;                /**/                tc = fc ^ 2;                q.push(tc);            }        }    }    return true;}
