void make_parent(int v, int p) {
  parent[v] = p;
  for (auto x : V[v]) {
    if (x != p) {
      make_parent(x, v);
    }
  }
}

int grundy(int v) {
  int s = V[v].size();
  if (v == 0) ++s;
  int ans;
  if (s == 1) {
    ans = 0;
  } else if (s == 2) {
    for (auto x : V[v]) {
      if (x != parent[v]) {
        ans = grundy(x) + 1;
      }
    }
  } else {
    ans = 0;
    for (auto x : V[v]) {
      if (x != parent[v]) {
        ans ^= (grundy(x) + 1);
      }
    }
  }
  // cerr << "grundy(" << v << ") = " << ans << endl;
  return ans;
}

