bool dfs(int now, int parent)
{
  bool ret = 0;
  //cout << now + 1 << " " << parent + 1 << endl;
  int cnt = 0; //xor
  for(int i = 0; i < tree[now].size(); i++) {
    if(tree[now][i] != parent) {
      cnt++;
    }
  }
  for(int i = 0; i < tree[now].size(); i++) {
    int u = tree[now][i];
    if(u != parent) {
      if(cnt == 1) ret |= dfs(u, now);
      else ret ^= dfs(u, now);
    }
  }

  //if(now == 0) cout << ret << endl;
  if(cnt == 1) ret = true;
  else if(!ret && cnt % 2) ret ^= true;
  // cout << " " << cnt << " " << ret << endl;
  return (ret);
}

