const int INF = 1e9 + 7;

int grandy(int r, int v,  vector <int> &gr, vector <vector <pair <int, int> > > &data){
    if (gr[r] != INF)
        return gr[r];
    int s = 0;
    for (auto elem : data[r]){
        if (elem.first == v)
            continue;
        if (gr[elem.first] == INF)
            gr[elem.first] = grandy(elem.first, r, gr, data);
        s = s ^ (gr[elem.first] + 1);
    }
    gr[r] = s;
    return gr[r];
}

int ffind(int n, int m, int v, vector <int> &gr, vector <vector <pair <int, int> > > &data){
    for (auto elem : data[n]){
        if (elem.first == v)
            continue;
        //cerr << n + 1 << " " << gr[n] << " " << m << " "  << elem.first + 1 << " " << gr[elem.first] + 1 << endl;
        //cerr << (gr[n] ^ m ^ (gr[elem.first] + 1))<< endl;
        if ((gr[n] ^ m ^ (gr[elem.first] + 1)) == 0)
            return elem.second;
        int x = ffind(elem.first, (gr[n] ^ m ^ (gr[elem.first] + 1)) - 1, n, gr, data);
        if (x != -1)
            return x;
    }
    return -1;
}

