        tree[v1].push_back(v2);
        tree[v2].push_back(v1);
    }
    tree[0].push_back(0);

    int res = rec(0, 0);
    if (res % 2 == 0) {
        printf("Bob");
    }else{
        printf("Alice");
    }
}

