using namespace std;

ll grundy(vector<vector<ll>>& connection, vector<bool>& already, ll now) {
  already.at(now) = true;
  vector<ll> res;
  for (ll i = 0; i < connection.at(now).size(); ++i) {
    ll next = connection.at(now).at(i);
    if (already.at(next)) {
      continue;
    }
    already.at(next) = true;
    res.push_back(grundy(connection, already, next));
  }
  if (res.size() == 1) {
    return res.at(0) + 1;
  }
  else {
    ll temp = 0;
    for (ll i = 0; i < res.size(); ++i) {
      temp ^= res.at(i);
    }
    return temp;
  }
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(nullptr);
  ll N;
  cin >> N;
  vector<vector<ll>> connection(N);
  vector<bool> already(N, false);
  for (ll i = 0; i < N - 1; ++i) {
    ll x, y;
    cin >> x >> y;
    x -= 1;y -= 1;
    connection.at(x).push_back(y);
    connection.at(y).push_back(x);
  }
  ll ans = grundy(connection, already, 0);
  if (ans == 0) {
    cout << "Bob" << "\n";
  }
  else {
    cout << "Alice" << "\n";
  }
}

