void rec(int u, vvi &g, vi &dis, vi &G) {	
	int Gu = 0;
	for (int v : g[u]) {
		if (dis[v] != -1) continue;
		dis[v] = dis[u] + 1;
		rec(v, g, dis, G);
		Gu ^= G[v] + 1;
	}	
	G[u] = Gu;
	// dbl(u); dbvv(G);
}
void dfs(int s, vvi &g, vi &dis, vi &G) {
	dis[s] = 0;
	rec(s, g, dis, G);
}

