vector<int> dfs()
{
    ms(visited,false);
    stack<int> s;
    visited[0] = true;
    s.push(0);
    vector<int> node_order;

    while (!s.empty()) {
        int top = s.top(); s.pop();
        node_order.pb(top);
        iter(adj[top]) {
            if (!visited[*it]) {
                visited[*it] = true;
                children[top].pb(*it);
                s.push(*it);
            }
        }
    }
    return node_order;
}

