        return make_pair(d[l], l) > make_pair(d[r], r);
    }
};

void dfs(int node, int pnode){
    p[node] = pnode;
    d[node] = d[pnode] + 1;
    for(auto &i : v[node]){
        if(i == pnode) continue;
