        deg[x]++; deg[y]++;
        v[x].push_back(y);
        v[y].push_back(x);
    }
    dfs(1, 0);
    set <int, cmp> ready;
    for(int i = 2 ; i <= n ; i++){
        if(deg[i] == 1) ready.insert(i);
    }
    while(ready.size()){
        if(ready.size() == 1) finish("Alice");
        int x = *ready.begin();
        ready.erase(ready.begin());
        int y = *ready.begin();
        ready.erase(ready.begin());
