template<class T, class S>
void dfs_node(Graph& graph,  ll startNode, T before_act, S after_act, T before_act2, S after_act2)
{
	// Impliment func: void(ll node_ind) representing what this should do, when target node moves from visited node to unvisited node (node_ind).

	vb visited(graph.size());
	auto dfs_impl = [&](auto dfs_impl, ll startNode, T before_act, S after_act)-> void {
		before_act(startNode);
		visited[startNode] = 1;
		for (auto& e : graph.out(startNode)) {
			if (visited[e->to])
				continue;
			dfs_impl(dfs_impl, e->to);
		}
		after_act(startNode);
		return;
	};
	dfs_impl(dfs_impl, startNode, before_act, after_act);
	dfs_impl(dfs_impl, startNode, before_act2, after_act2);

};


