using namespace std;

int find_win(vector<int> &g, vector< vector< pair<int, int > > > &list, int v, int pr, int u1, int u2) {
	int xr = 0;
	for (int i = 0; i < list[v].size(); i++) {
		int nxt = list[v][i].first;
		if ((v == u1 && nxt == u2) || (v == u2 && nxt == u1)) {
			continue;
		}
		if (nxt != pr) {
			xr ^= (find_win(g, list, nxt, v, u1, u2) + 1);
		}
	}
	g[v] = xr;
	return xr;
}

int main() {
	ios_base::sync_with_stdio(0);
