	vector< vector< pair<int, int > > > list(n + 1, vector< pair<int, int > >());
	for (int i = 0; i < n - 1; i++) {
		cin >> a >> b;
		list[a].push_back({ b, i });
		list[b].push_back({ a, i });
	}
	vector<int> g(n + 1, 0);
	int w = find_win(g, list, r, -1, -1, -1);
	if (w > 0) {
		cout << "Alice" << endl;
	} else {
		cout << "Bob";
	}
	return 0; 
}

