int dp[100005];

int calc(int p, int x){
  if(dp[p] != -1) return dp[p];
  int ret = 0;
  vector<int> res;
  rep(i,G[p].size()){
    if(G[p][i] == x) continue;
    res.push_back(calc(G[p][i], p));
  }
  if(res.size()==1) ret = res[0] + 1;
  else{
    rep(i,res.size()){
      ret ^= res[i];
    }
  }
  return dp[p] = ret;
}

