using Graph = vector<vector<int> >;
int N;
Graph G;

int solve(int v = 0, int p = -1) {
    int res = 0;
    for (auto e : G[v]) {
        if (e == p) continue;
        res ^= solve(e, v);
    }

    //cout << v << ": " << res + 1 << endl;
    return res+1;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    while (cin >> N) {
        G.assign(N, vector<int>());
        for (int i = 0; i < N-1; ++i) {
            int x, y; cin >> x >> y; --x, --y;
            G[x].push_back(y);
            G[y].push_back(x);
        }
        int grundy = solve() - 1;
        if (grundy) cout << "Alice" << endl;
        else cout << "Bob" << endl;
    }
}

