using Graph = vector<vector<int>>;
const int MAX_N = 100010;
Graph g(MAX_N);

int dfs(int cur, int pre) {
  if ((int)g[cur].size() == 1) {
    if (cur == 0) return 1;
    return 0;
  }
  int ch = g[cur].size();
  if (cur != 0) ch--;
  int ok = 0;
  for (auto nxt : g[cur]) {
    if (nxt == pre) continue;
    ok ^= (1+dfs(nxt, cur));
  }
  return ok;
}

