ll dfs(vvl &G, ll now, ll par) {
  ll po = 0;
  ll count = 0;
  AUTO(g, G[now]) {
    if (g == par)
      continue;
    po ^= dfs(G, g, now) + 1;
    count++;
  }
  // cout << now + 1 << po << endl;
  return po;
}

