bool dfs(int u, int p, std::vector<int> Adj[])
{
  int win = 0;
  int lose = 0;

  for(int i = 0; i < Adj[u].size(); i++)
  {
    int v = Adj[u][i];
    if(v != p)
    {
      bool add = dfs(v, u, Adj);
      win += add;
      lose += !add;
    }
  }

  return (win%2 || lose%2);
} 

