const int MAX_N = 1e5;
vector<int> es[MAX_N];
bool used[MAX_N];

//nownowgrundy
int grundy(int now){
    used[now] = true;
    vector<int> child;
    for(auto &e: es[now]){
        if(!used[e]) child.pb(e);
    }
    if(child.empty()) return 0;
    elif(child.size() == 1) return grundy(child[0])+1;
    else{
        int res = 0;
        for(auto &e: child) res ^= grundy(e);
        return res;
    }
}

