void pretr(int root, int fa) {
    son[root] = 0;
    for (int i = 0;i < v[root].size();i++) {
        int to = v[root][i];
        if (to == fa) continue;
        pretr(to, root);
        son[root]++;
    }
}
void dfs(int root, int fa) {
    if (son[root] == 0) {
        sg[root] = 0;
        return;
    }
    if (son[root] == 1) {
        for (int i = 0;i < v[root].size();i++) {
            int to = v[root][i];
            if (to != fa) {
                dfs(to, root);
                sg[root] = sg[to] + 1;
                return;
            }
        }
    }
    else {

        sg[root] = 0;
        for (int i = 0;i < v[root].size();i++) {
            int to = v[root][i];
            if (to == fa) continue;
            dfs(to, root);
            sg[root] ^= sg[to];
        }
    }
}
