int dfs(int now, int par, vector<vector<int>>& g) {
	int ret = 0;
	for (auto nx : g[now]) {
		if (nx == par) continue;
		ret ^= dfs(nx, now, g);
	}
	if (now == 0) return ret;
	return ret + 1;
}


