typedef long long ll;
vector<ll> node[100005];
bool used[100005]={false};
ll grundy(ll pos){
  if(pos==1){
    ll X=0;
    for(int i=0;i<node[pos].size();i++){
      ll to=node[pos][i];
      used[to]=true;
      X^=grundy(to);
    }
    if(X==0){
      cout<<"Bob"<<endl;
    }
    else{
      cout<<"Alice"<<endl;
    }
    return 0;
  }
  else{
    vector<ll> vec;
    for(int i=0;i<node[pos].size();i++){
      ll to=node[pos][i];
      if(!used[to]){
        used[to]=true;
        vec.push_back(grundy(to));
      }
    }
    sort(vec.begin(),vec.end());
    vec.erase(unique(vec.begin(),vec.end()),vec.end());
    ll res=0;
    for(int i=0;i<vec.size();i++){
      ll tp=vec[i];
      if(res!=tp) break;
      res++;
    }
    return res;
  }
}
