class Random {
    public:
    static unsigned get(unsigned x) {
        return xorshift() % x;
    }

    private:
    inline static unsigned xorshift() {
        static unsigned x = 123456789, y = 362436039, z = 521288629, w = 88675123;
        unsigned t = x ^ (x << 11);
        x = y, y = z, z = w;
        return w = (w ^ (w >> 19)) ^ (t ^ (t >> 8));
    }
};

int mod = 1000000007;
long long r[100000];
vector <int> v[100000];

pair<int, long long> dfs(int x, int y) {
    int m = 0, i;
    long long z = 1;
    set <pair<int, long long> > s;
    set <pair<int, long long> >::iterator it;

    for (i = 0; i < v[x].size(); i++) {
        pair<int, long long> p;

        if (v[x][i] == y) continue;

        p = dfs(v[x][i], x);

        if (s.count(p)) {
            s.erase(p);
        } else {
            s.insert(p);
        }
    }

    if (s.size() == 0) return make_pair(0, 1);

    for (it = s.begin(); it != s.end(); it++) m = max(m, it->first + 1);
    for (it = s.begin(); it != s.end(); it++) z = z * (r[m] + it->second) % mod;

    return make_pair(m, z);
}

