int first[maxn];

inline void add_edge(int from, int to)
{
	static int cnt = 0;
	e[++cnt].nxt = first[from];
	first[from] = cnt;
	e[cnt].to = to;
	e[++cnt].nxt = first[to];
	first[to] = cnt;
	e[cnt].to = from;
}

int sg[maxn];
int n;

inline void dfs(int now, int fa)
{
	int gs = 0;
	for (int i = first[now]; i; i = e[i].nxt)
	{
		register int to = e[i].to;
		if (to != fa)
		{
			gs++;
			dfs(to, now);
		}
	}
	if(gs == 1)
	{
		for (int i = first[now]; i; i = e[i].nxt)
			if (e[i].to != fa)
				sg[now] = sg[e[i].to] + 1;
	}
	else
	{
		for (int i = first[now]; i; i = e[i].nxt)
			if (e[i].to != fa)
				sg[now] ^= sg[e[i].to];
	}
}

