int solve(vector<vector<int>>& ne) {
    vector<int> xors;

    int N = ne.size();
    vector<int> parent(N);
    vector<vector<int>> children(N);
    parent[0] = -1;
    queue<int> q;
    q.push(0);
    vector<int> r;
    vector<int> visited(N, false);
    while (!q.empty()){
        int u = q.front(); q.pop();
        r.push_back(u);
        visited[u] = true;
        for (int v : ne[u]) {
            if (visited[v]) continue;
            parent[v] = u;
            children[u].push_back(v);
            q.push(v);
        }
    }
    reverse(ALL(r));
    vector<vector<int>> legs(N);
    // cout << parent << endl;
    // cout << r << endl;
    rep(i,N) {
        int v = r[i], u = parent[v];

        int L = legs[v].size();
        int msg = -1;
        switch (L) {
            case 0:
                msg = 1;
                if (v == 0) xors.push_back(msg);
                break;
            case 1:
                msg = legs[v][0] + 1;
                if (v == 0) xors.push_back(msg);
                break;
            default:
                int x = 0;
                rep(i, L) x ^= legs[v][i];
                xors.push_back(x);
                msg = 1;
                break;
        }
        if (u != -1 && msg != -1) {
            legs[u].push_back(msg);
        }
    }

    int ans = 0;
    for (int x : xors) ans += x;

    return ans;
}

