const int maxn = 1e5 + 37;

int gn[maxn];
vector <int> t[maxn];

void dfs (int v, int p = -1) { // find grundy_number
	if (t[v].size() == 2 and p != -1) {
		for (auto w : t[v]) {
			if (w != p) {
				dfs (w, v);
				gn[v] = gn[w] + 1;
			}
		}
	}
	else {
		for (auto w : t[v]) {
			if (w != p) {
				dfs (w, v);
				gn[v] ^= gn[w];
			}
		}
	}
//	cout << v << " " << gn[v] << endl;
}

int main() {
	ios_base::sync_with_stdio(false);
	int n;
	cin >> n;
	for (int i = 1; i <= n - 1; i++) {
		int v, u;
		cin >> v >> u;
		t[v].PB (u);
		t[u].PB (v);
	}
	dfs (1);
	if (gn[1] == 0)
		cout << "Bob" << endl;
	else
		cout << "Alice" << endl;
}

