#include<bits/stdc++.h>
using namespace std;


int dfs(int v,vector<vector<int>> &edge,vector<int> &grundy,vector<int> &used){
    used[v]=true;
    int res=0;
    for(int i=0;i<edge[v].size();i++){
        int to=edge[v][i];
        if(used[to]){
            continue;
        }
        res=min(dfs(to,edge,grundy,used),res);
    }
    return grundy[v]=res+1;
}

