const int N=100000+19;

struct Edge{
	int y,nxt;
} E[N*2];
int las[N];
int n,cnt;

void Link(int x,int y){
	E[cnt]=(Edge){y,las[x]};las[x]=cnt++;
	E[cnt]=(Edge){x,las[y]};las[y]=cnt++;
}
int dfs(int x,int fa){
	int res=0;
	for (int i=las[x],y;~i;i=E[i].nxt)
		if ((y=E[i].y)!=fa){
			res^=dfs(y,x)+1;
		}
	return res;
}

