int N;
vector<vector<int>> E;

int grundy[100000 + 100];

int dfs(int pos, int bef) {
	if (grundy[pos] != -1)return grundy[pos];

	int g = 0;
	for (int i = 0; i < E[pos].size(); i++) {
		int nxt = E[pos][i];
		if (nxt == bef)continue;
		g ^= (dfs(nxt, pos) + 1);
	}
	return grundy[pos] = g;
}

int main() {

	cin >> N;
	E.resize(N);
	for (int i = 0; i < N - 1; i++) {
