		E[u].push_back(v), E[v].push_back(u);
	}

	for (int i = 0; i < N; i++)grundy[i] = -1;
	dfs(0, -1);

	if (grundy[0])cout << "Alice" << endl;
	else cout << "Bob" << endl;
	return 0;
}

