int n, i, sum, x, y;
int s[DIM], w[DIM];
vector<int> v[DIM];
void dfs(int nod, int t){
    int i, nr = 0;
    for(i = 0; i < v[nod].size(); i++){
        if(v[nod][i] != t){
            dfs(v[nod][i], nod);
        }
    }
    for(i = 0; i < v[nod].size(); i++){
        if(v[nod][i] != t){
           w[++nr] = s[ v[nod][i] ];
        }
    }
    sort(w + 1, w + nr + 1);
    if(w[1] == 0){
        for(i = 2; i <= nr; i++){
            if(w[i] - w[i - 1] > 1){
                s[nod] = w[i - 1] + 1;
                break;
            }
        }
        if(i == nr + 1){
            s[nod] = w[nr] + 1;
        }
    }
}
int main(){
    cin>> n;
    for(i = 1; i < n; i++){
        cin>> x >> y;
        v[x].push_back(y);
        v[y].push_back(x);
    }
    if(v[1].size() == 1){
        cout<<"Alice";
        return 0;
    }
    dfs(1, 0);
    for(i = 0; i < v[1].size(); i++){
        sum ^= s[ v[1][i] ];
    }
    if(sum == 0){
        cout<<"Bob";
    }
    else{
        cout<<"Alice";
    }
}

