	int c1 = 0, c2 = 0;
	for (int i = lst[u]; i; i = g[i].nxt) {
		int v = g[i].to; if (v == ff) continue;
		dfs(v, u);
		if (f[v]) c1++;
		else c2++;
	}
	if (!c1) f[u] = c2 & 1;
	else f[u] = c1 & 1;
	//printf("[%d] %d\n", u, f[u]);
}
