	int child = 0;
	vector <int> son;
	for(auto u : g[at]){
		if(u == par) continue;
		++child;
		son.push_back(hack(u, at));
	}
	int ret;
	if(son.size() == 0) ret = 1;
	if(son.size() == 1) ret = son[0] + 1;
	else{
		int X = 0;
		for(auto u : son) X ^= u;
		ret = X + 1;
	}
	return ret;
}

