bool used[100005]={false};
ll grundy(ll pos){
  ll cnt=0;
  for(int i=0;i<node[pos].size();i++){
    ll to=node[pos][i];
    if(!used[to]) cnt++;
  }
  if(cnt==0) return 0;
  else if(cnt==1){
    for(int i=0;i<node[pos].size();i++){
      ll to=node[pos][i];
      if(!used[to]){
        used[to]=true;
        return grundy(to)+1;
      }
    }
  }
  else{
    ll res=0;
    for(int i=0;i<node[pos].size();i++){
      ll to=node[pos][i];
      if(!used[to]){
        used[to]=true;
        res^=grundy(to);
      }
    }
    return res;
  }
}
