constexpr T INF = numeric_limits<T>::max() / 100;
constexpr int MAX = 100000;

struct Graph {
    void addEdge(int from, int to)
    {
        edge[from].push_back(to);
    }
    vector<int> edge[MAX];
};

int N;
Graph g_;
Graph g;

ll grundy(int root)
{
    if (g.edge[root].empty()) {
        return 0;
    } else if (g.edge[root].size() == 1) {
        return grundy(g.edge[root][0]) + 1;
    } else {
        ll num = 0;
        for (const int p : g.edge[root]) {
            num ^= grundy(p);
        }
        return num;
    }
}

int main()
{
    cin >> N;
