class Graph {
public:
	int vn;
	int sumcost = 0;
	vector<vector<pii>> g;

	Graph(int n) {
		vn = n;
		g.resize(n);
	}
	virtual void con(int a, int b, int w) = 0;
	int getWeight(int f, int t) {
		auto itr = lower_bound(ALL(g[f]), make_pair(t, INT_MIN));
		if (itr != g[f].end())
			return itr->second;
		return INT_MIN;
	}
	int Costsum() {
		return sumcost;
	}
	void scan(int edcount, bool oindexed, bool w) {
		REP(i, edcount) {
			int a, b, c = 1;
			scanf("%d %d", &a, &b);
			if (w)scanf("%d", &c);
			con(a - oindexed, b - oindexed, c);
		}
	}
};
class BiDGraph : public Graph {//
public:
	BiDGraph(int n) : Graph(n) {}

	void con(int a, int b, int w = 1) {
		g[a].push_back({ b,w });
		g[b].push_back({ a, w });
		sumcost++;
	}
};

signed main() {
	int n;
	scanf("%d", &n);
	BiDGraph g(n);
	vector<int> ind(n);
	g.scan(n-1, 1, 0);


	function<void(int, int)> inn = [&](int c, int p) {
		REP(i, g.g[c].size()) {
			ind[g.g[c][i].first] = i;
		}
		for (auto itr : g.g[c]) {
			if (itr.first != p) {
				inn(itr.first, c);
			}
		}
	};
	inn(0, -1);


	vector<int> gr(n,INT_MAX);

	gr[g.g[0][0].first] = 0;
	int last = 0;

	function<int(int, int)> dfs = [&](int c, int p) {
		if(p >= 0)
			gr[c] = min(gr[c], gr[p]);
		last = max(last, c);

		if ((ind[c] - 1) >= 0) {
			gr[c] = min(gr[g.g[p][ind[c] - 1].first], gr[c]);
		}
		int ret = gr[c];

		for (auto itr : g.g[c]) {
			if (itr.first != p) {
				ret = min(ret,dfs(itr.first, c));
				gr[itr.first] = min(ret, gr[c]);
			}
		}
		return ret;
	};

	dfs(0, -1);
	printf(!gr[last] ? "Alice\n" : "Bob\n");
	return 0;
}

