const int MAX = 100010;
int N;
int x[MAX], y[MAX];
VI graph[MAX];

int dfs(int now, int prev) {
    int res = 0;
    FORE(e, graph[now]) {
        if (e != prev) {
            ++res;
            res += dfs(e, now);
        }
    }
    return res;
}

int main(void) {
    scanf("%d", &N);
    int oneCnt = 0;
    REP(i, N - 1) {
        scanf("%d%d", x + i, y + i);
        oneCnt += ((x[i] == 1) || (y[i] == 1));
        x[i]--;
        y[i]--;
        graph[x[i]].EB(y[i]);
        graph[y[i]].EB(x[i]);
    }
    if (oneCnt == 1) {
        cout << "Alice" << endl;
        return 0;
    }
    if (oneCnt == 2) {
        VI ch;
        FORE(e, graph[0]) ch.EB(dfs(e, 0) + 1);
        cout << (ch[0] % 2 == ch[1] % 2 ? "Bob" : "Alice") << endl;
        return 0;
    }
    else assert(false);
}

