ll g[100010];

bool dfs( ll v, ll p = -1 ) {
  g[v] = 0;

  bool used[2] = { false, false };
  bool leaf = true;
  ll cnt = 0;

  for( auto u : G[v] ) if( u != p ) {
    bool fl = dfs( u, v );

    ++cnt;
    leaf = false;

    if( fl ) 
      continue;

    g[u] %= 2;
    g[v] += g[u];
    g[v] %= 2;
  }

  if( leaf )
    return leaf;

  if( cnt == 1 )
    g[v] = 1;
  else if( cnt%2 == 1 && used[1] && !g[v] )
    g[v] = 1;

  return leaf;
}

