ll dfs(int a)
{
	visited[a] = true;
	vector<ll> G;
	for(auto &u : graph[a])
	{
		if(!visited[u])
		{
			G.pb(dfs(u));
		}
	}
	if(G.size() == 1)
		return 1 + G[0];
	else if(G.size() == 0)
		return 0;
	ll s = 0;
	for(auto &g : G)
	{
		s ^= g;
	}
	return s;
}

