vector<int> g[100010];


int grundy(int n, int p) {
    set<int> G;
    for (auto i : g[n]) {
        if (i != p) {
            G.insert(grundy(i, n));
        }
    }
    int s = 0;
    while (G.count(s)) s++;
    return s;

}

void solve(long long N, std::vector<long long> x, std::vector<long long> y) {
    for (int i = 0; i < N - 1; i++) {
        x[i]--;
        y[i]--;
        g[x[i]].push_back(y[i]);
        g[y[i]].push_back(x[i]);
    }
    int ans = 0;
    for (auto i : g[0]) {
        ans ^= grundy(i, 0);
    }
    if (ans) cout << "Alice" << endl;
    else cout << "Bob" << endl;
}

