int hackenbush ( int s, int p ) {

    int res = 0;
    FOR(i,0,SZ(adj[s])-1) {
        int t = adj[s][i];

        if ( t == p ) continue;

        res ^= hackenbush ( t, s ) + 1;
    };

    return res;
}

void solution() {
    int kase;
    //scanf ( "%d", &kase );

    //assert ( kase >= 1 && kase <= 100 );
    kase = 1;
    while ( kase-- ) {
        int n;
