        FOR(i,1,n-1) {
            int u, v;
            scanf ( "%d %d", &u, &v );
            adj[u].pb ( v );
            adj[v].pb ( u );			
        };

        int res = hackenbush ( 1, 0 );

        if ( res ) printf ( "Alice\n" );
        else printf ( "Bob\n" );
    }
}

