void dfs(int pos, int bef, int depth) {
	if (depth == 2) {
		return;
	}
	int c = 0;
	for (int i = 0; i < E[pos].size(); i++) {
		int nxt = E[pos][i];
		if (nxt == bef)continue;
		dfs(nxt, pos, depth);
		if (depth == 1) {
			c++;
		}
	}
	if (depth == 1)cnt.push_back(c);
}

