using namespace std;

const int N = 111111;
vector<int> tree[N];
int pl[N], xs[N];
int n;

void dfs(int node, int p = 0) {
    for(nnode : tree[node]) {
        if(nnode == p) continue;
        dfs(nnode, node);
    }
    int nei = tree[node].size() - (node != 1);
    if(nei == 0) {
        pl[node] = 1;
    } else if(nei == 1) {
        int nnode = tree[node][0] == p ? tree[node][1] : tree[node][0];
        pl[node] = pl[nnode] + 1;
        xs[node] = xs[nnode];
    } else if(nei > 1) {
        for(nnode : tree[node]) {
            xs[node] ^= xs[nnode] ^ pl[nnode];
        }
        pl[node] = 1;
    }
}

