using namespace std;

vector< vector<int> > tree(100000, vector<int>(0));

int rec(int node, int prev) {
    //printf("now on %d\n", node);
    //printf("size = %d\n", tree[node].size());
    if (tree[node].size() % 2 == 0) {
        return 1;
    }

    int ans = 0;
    rep(i, tree[node].size()) {
        int child = tree[node][i];
        if (child != prev)
            ans += rec(child, node);
    }
    return ans;
}

